import React, { useState } from "react";
import './game1.css'
function Game() {
    const [color, setColor] = useState("red");

    return (
                
    <>
        <h1>My favorite color is {color}!</h1>
        <button
            type="button"
            className="a"
            onClick={(e) => setColor(color==="blue"? e.style.color:"#1b3eec" )}
        >Blue
        </button>
        <button
            type="button"
            className="b"
            onClick={() => setColor("red")}
        >Red</button>
        <button
            type="button"
            className="c"
            onClick={() => setColor("pink")}
        >Pink</button>
        <button
            type="button"
            className="d"
            onClick={() => setColor("green")}
        >Green</button>
    </>
    );
}
export default Game;
